import styled from "styled-components";

export const Container = styled.div`
  > div {
    .bg-skeleton {
      width: 100%;
      height: 70px;

      filter: brightness(96%);
    }

    span {
      display: flex;
      flex-direction: column;
      align-items: center;
      padding-bottom: 175px;
    }
  }
`;
